content.script:
function showImage(img){ return (function(){ img.style.display='inline'; }) }
function notLinkroll(element) { return element != 'linkroll'; }
function filter(f, a) {
    var r=[];
    for (var i=0; i!=a.length; i++) {
        if (f(a[i])) r.push(a[i]);
    }
    return r;
}
var dl = document.createElement('dl');
dl.className = 'linkroll';
for (var i=0, post; post = Delicious.posts[i]; i++) {
    var dt = document.createElement('dt');
    var dd = document.createElement('dd');
    var img = document.createElement('img')
    var a = document.createElement('a');
    var tags = document.createElement('span');

    tags.className = 'tags';
    img.style.display = 'none'
    img.height = img.width = 16;
    img.src = post.u.split('/').splice(0,3).join('/')+'/favicon.ico';
    img.onload = showImage(img);
    a.setAttribute('href', post.u);
    a.appendChild(img);
    a.appendChild(document.createTextNode(post.d));
    tags.appendChild(document.createTextNode('(' + filter(notLinkroll, post.t).join(', ') + ')'));

    dt.appendChild(a);
    dt.appendChild(document.createTextNode(' '));
    dt.appendChild(tags);
    dd.appendChild(document.createTextNode(post.n));
    dl.appendChild(dt);
    dl.appendChild(dd);
}
document.getElementById('linkroll').appendChild(dl);
