content.script:
var ul = document.createElement('ul');
ul.className = 'photoblog';
for (var i=0, image; image = Kiek.images[i]; i++) {
    var li = document.createElement('li');
    var a = document.createElement('a');
    var img = document.createElement('img');

    img.setAttribute('src', image.thumbnail);
    img.setAttribute('alt', image.title);

    a.className = 'external';
    a.setAttribute('href', image.url);
    a.appendChild(img);

    li.appendChild(a);
    ul.appendChild(li);
}
document.getElementById('photoblog').appendChild(ul);

var dl = document.createElement('dl');
dl.className = 'linkroll';
for (var i=0, post; post = Magnolia.bookmarks[i]; i++) {
    var dt = document.createElement('dt');
    var dd = document.createElement('dd');
    var a = document.createElement('a');

    a.className = 'external';
    a.setAttribute('href', post.url);
    a.appendChild(document.createTextNode(post.title));

    dt.appendChild(a);
    dd.appendChild(document.createTextNode(post.description));
    dl.appendChild(dt);
    dl.appendChild(dd);
}
document.getElementById('linkroll').appendChild(dl);
